#!/usr/bin/env bash # Run fast evaluation with optimizations # Usage: ./scripts/evaluation/run_fast_evaluation.sh [--game magic] [--quick] set -euo pipefail GAME="${1:-magic}" QUICK="${2:-}" if [ "$QUICK" == "--quick" ]; then TEST_SET="experiments/test_substitutions_quick.json" OUTPUT="experiments/downstream_eval_${GAME}_fast_quick.json" else TEST_SET="experiments/test_set_unified_${GAME}.json" OUTPUT="experiments/downstream_eval_${GAME}_fast.json" fi echo "Running FAST evaluation (functional tags disabled)..." echo "Game: $GAME" echo "Test set: $TEST_SET" echo "Output: $OUTPUT" echo "" time uv run python src/ml/scripts/evaluate_downstream_complete.py \ --game "$GAME" \ --embeddings "data/embeddings/game_specific/${GAME}_128d_test_pecanpy.wv" \ --pairs "data/processed/pairs_large.csv" \ --test-substitutions "$TEST_SET" \ --output "$OUTPUT" \ --fast echo "" echo " Fast evaluation complete!" echo "Results: $OUTPUT"
