#!/usr/bin/env python3 """Download files from S3 for hyperparameter search.""" import boto3 import os import sys s3 = boto3.client('s3') bucket = 'games-collections' os.makedirs('/tmp/hyperparam_search', exist_ok=True) files_to_download = [ ('scripts/hyperparameter_search_standalone.py', '/tmp/hyperparam_search/hyperparameter_search_standalone.py'), ('processed/pairs_large.csv', '/tmp/hyperparam_search/pairs_large.csv'), ('processed/test_set_unified_magic.json', '/tmp/hyperparam_search/test_set.json'), ('processed/name_mapping.json', '/tmp/hyperparam_search/name_mapping.json'), ] for s3_key, local_path in files_to_download: try: s3.download_file(bucket, s3_key, local_path) print(f' Downloaded {s3_key}') except Exception as e: print(f'Warning: Could not download {s3_key}: {e}') print('Download complete') sys.exit(0)
